import React, { useState } from "react";
import { BrowserRouter as Router, Routes, Route, useNavigate } from "react-router-dom";
import GridView from "./GridView";
import HomePage from "./HomePage";
import ResultPage from "./ResultPage"; // ResultPageのインポート


const App = () => {
  const [pin, setPin] = useState(null);

  return (
    <Router>
      <div className="App">
        <Routes>
          <Route path="/" element={<HomePage onStart={() => navigate("/grid")} />} />
          <Route
            path="/grid"
            element={<GridView setPin={setPin} onShowResult={() => navigate("/result")} />}
          />
          <Route
            path="/result"
            element={<ResultPage pin={pin} onBack={() => navigate("/grid")} />}
          />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
