import React, { useState, useRef, useEffect } from "react";
import { TransformWrapper, TransformComponent } from "react-zoom-pan-pinch";
import { FixedSizeGrid as Grid } from "react-window";
import "./styles.css"; // CSS ファイルのインポート

const calculateDistance = (x1, y1, x2, y2) => {
  return Math.sqrt(Math.pow(x2 - x1, 2) + Math.pow(y2 - y1, 2)).toFixed(2);
};

// 定数座標

const GridView = ({ onBack, onShowResult, setPin }) => {
  const [scrollOffset, setScrollOffset] = useState({
    scrollLeft: 0,
    scrollTop: 0,
  });
  const [cursorPosition, setCursorPosition] = useState({ x: 0, y: 0 });
  const [scale, setScale] = useState(1);
  const [isDragging, setIsDragging] = useState(false);
  const [startDragPosition, setStartDragPosition] = useState({ x: 0, y: 0 });
  const [referencePoint, setReferencePoint] = useState({ x: 500, y: 400 });
  const [pin, setLocalPin] = useState(null);

  const gridWrapperRef = useRef(null);

  useEffect(() => {
    const handleMouseMove = (event) => {
      if (gridWrapperRef.current) {
        const gridRect = gridWrapperRef.current.getBoundingClientRect();
        const x = event.clientX - gridRect.left;
        const y = event.clientY - gridRect.top;
        setCursorPosition({ x, y });
      } else {
        setCursorPosition({ x: "null", y: "null" });
      }
    };

    const handleMouseDown = (event) => {
      if (gridWrapperRef.current) {
        const gridRect = gridWrapperRef.current.getBoundingClientRect();
        setStartDragPosition({
          x: event.clientX - gridRect.left,
          y: event.clientY - gridRect.top,
        });
        setIsDragging(false);
      }
    };

    const handleMouseUp = (event) => {
      if (gridWrapperRef.current) {
        const gridRect = gridWrapperRef.current.getBoundingClientRect();
        const x = event.clientX - gridRect.left;
        const y = event.clientY - gridRect.top;

        if (!isDragging) {
          const adjustedX = (x + scrollOffset.scrollLeft) / scale;
          const adjustedY = (y + scrollOffset.scrollTop) / scale;
          setPin({ x: adjustedX, y: adjustedY });
          setLocalPin({ x: adjustedX, y: adjustedY });
        }
      }
    };

    const handleMouseMoveDrag = (event) => {
      if (gridWrapperRef.current) {
        const gridRect = gridWrapperRef.current.getBoundingClientRect();
        const x = event.clientX - gridRect.left;
        const y = event.clientY - gridRect.top;
        const dragDistance = Math.sqrt(
          Math.pow(x - startDragPosition.x, 2) +
            Math.pow(y - startDragPosition.y, 2)
        );
        if (dragDistance > 5) {
          setIsDragging(true);
        }
      }
    };

    window.addEventListener("mousemove", handleMouseMove);
    window.addEventListener("mousemove", handleMouseMoveDrag);
    window.addEventListener("mousedown", handleMouseDown);
    window.addEventListener("mouseup", handleMouseUp);

    return () => {
      window.removeEventListener("mousemove", handleMouseMove);
      window.removeEventListener("mousemove", handleMouseMoveDrag);
      window.removeEventListener("mousedown", handleMouseDown);
      window.removeEventListener("mouseup", handleMouseUp);
    };
  }, [scrollOffset, scale, isDragging, startDragPosition]);

  return (
    <div ref={gridWrapperRef} className="grid-container">
      <button
        onClick={onBack}
        style={{
          position: "absolute",
          top: 10,
          left: 10,
          zIndex: 1000,
          backgroundColor: "#007BFF",
          color: "#fff",
          border: "none",
          borderRadius: "5px",
          padding: "10px 20px",
          cursor: "pointer",
        }}
      >
        Back
      </button>
      <button
        onClick={onShowResult}
        style={{
          position: "absolute",
          top: 10,
          left: 100,
          zIndex: 1000,
          backgroundColor: "#28A745",
          color: "#fff",
          border: "none",
          borderRadius: "5px",
          padding: "10px 20px",
          cursor: "pointer",
        }}
      >
        結果
      </button>
      <TransformWrapper
        options={{ limitToBounds: false }}
        onZoomStop={({ state }) => setScale(state.scale)}
        onPanningStop={({ state }) => {
          setScrollOffset({
            scrollLeft: -state.positionX,
            scrollTop: -state.positionY,
          });
        }}
      >
        <TransformComponent>
          <div
            style={{
              width: 50 * 100,
              height: 50 * 100,
              position: "relative",
              zIndex: 1,
            }}
          >
            <Grid
              className="Grid"
              columnCount={50}
              columnWidth={100}
              height={50 * 100}
              rowCount={50}
              rowHeight={100}
              width={50 * 100}
              style={{ overflow: "hidden", position: "absolute" }}
            >
              {({ columnIndex, rowIndex, style }) => (
                <div
                  style={{
                    ...style,
                    display: "flex",
                    alignItems: "center",
                    justifyContent: "center",
                    backgroundColor:
                      (columnIndex + rowIndex) % 2 === 0
                        ? "#f8f8f0"
                        : "#ffffff",
                  }}
                >
                  {/* ここにセルの内容を記述 */}
                </div>
              )}
            </Grid>
          </div>
        </TransformComponent>
      </TransformWrapper>
      {pin && (
        <div
          className="pin"
          style={{
            position: "absolute",
            left: pin.x * scale - scrollOffset.scrollLeft,
            top: pin.y * scale - scrollOffset.scrollTop,
            backgroundColor: "red",
            width: "10px",
            height: "10px",
            borderRadius: "50%",
            transform: "translate(-50%, -50%)",
            pointerEvents: "none",
          }}
        >
          <div
            style={{
              position: "absolute",
              bottom: "100%",
              left: "50%",
              transform: "translateX(-50%)",
              backgroundColor: "white",
              padding: "2px 5px",
              borderRadius: "3px",
              fontSize: "12px",
              boxShadow: "0 0 2px rgba(0,0,0,0.3)",
            }}
          >
            Distance:{" "}
            {pin
              ? calculateDistance(
                  pin.x,
                  pin.y,
                  referencePoint.x,
                  referencePoint.y
                )
              : "N/A"}{" "}
            px
          </div>
        </div>
      )}
      <div
        className="cursor-position"
        style={{
          position: "absolute",
          left: cursorPosition.x * scale - scrollOffset.scrollLeft,
          top: cursorPosition.y * scale - scrollOffset.scrollTop,
          backgroundColor: "lightyellow",
          padding: "2px 5px",
          borderRadius: "3px",
          fontSize: "12px",
          boxShadow: "0 0 2px rgba(0,0,0,0.3)",
          pointerEvents: "none",
        }}
      >
        Cursor Position: X: {cursorPosition.x.toFixed(2)} px, Y:{" "}
        {cursorPosition.y.toFixed(2)} px
      </div>
    </div>
  );
};

const HomePage = ({ onStart }) => (
  <div>
    <h1>Welcome to the Grid App</h1>
    <button onClick={onStart}>Start</button>
  </div>
);

const ResultPage = ({ pin, onBack }) => (
  <div>
    <h1>Results</h1>
    {pin ? (
      <div>
        <p>Pin Position:</p>
        <p>X: {pin.x.toFixed(2)}</p>
        <p>Y: {pin.y.toFixed(2)}</p>
      </div>
    ) : (
      <p>No pin set</p>
    )}
    <button onClick={onBack}>Back</button>
  </div>
);

const App = () => {
  const [showGrid, setShowGrid] = useState(false);
  const [showResult, setShowResult] = useState(false);
  const [pin, setPin] = useState(null);
  const [constantCoordinates] = useState({ x: 1000, y: 1000 }); // 定数座標

  const handleShowGrid = () => {
    setShowGrid(true);
    setShowResult(false);
  };

  const handleShowResult = () => {
    setShowResult(true);
  };

  const handleBack = () => {
    setShowGrid(false);
    setShowResult(false);
  };

  return (
    <div className="App">
      {showResult ? (
        <ResultPage
          pin={pin}
          constantCoordinates={constantCoordinates}
          onBack={handleBack}
        />
      ) : showGrid ? (
        <GridView
          onBack={handleBack}
          onShowResult={handleShowResult}
          setPin={setPin}
        />
      ) : (
        <HomePage onStart={handleShowGrid} />
      )}
    </div>
  );
};

export default App;
