const ResultPage = ({ pin, onBack }) => (
    <div className="result-container">
      <h1>Results</h1>
      {pin ? (
        <div>
          <p>Pin Position:</p>
          <p>X: {pin.x.toFixed(2)}</p>
          <p>Y: {pin.y.toFixed(2)}</p>
        </div>
      ) : (
        <p>No pin setきくらげ</p>
      )}
  
      {/* GridView と同様のグリッド表示 */}
      <div style={{ position: "relative", marginTop: "20px" }}>
        <TransformWrapper options={{ limitToBounds: false }}>
          <TransformComponent>
            <div
              style={{
                width: 50 * 100,
                height: 50 * 100,
                position: "relative",
                zIndex: 1,
              }}
            >
              <Grid
                className="Grid"
                columnCount={50}
                columnWidth={100}
                height={500}
                rowCount={50}
                rowHeight={100}
                width={500}
                style={{ overflow: "hidden", position: "relative" }}
              >
                {({ columnIndex, rowIndex, style }) => (
                  <div
                    style={{
                      ...style,
                      display: "flex",
                      alignItems: "center",
                      justifyContent: "center",
                      backgroundColor:
                        (columnIndex + rowIndex) % 2 === 0
                          ? "#f8f8f0"
                          : "#ffffff",
                    }}
                  >
                    {/* グリッド内のセルの内容を表示する部分 */}
                    Cell ({columnIndex}, {rowIndex})
                  </div>
                )}
              </Grid>
            </div>
          </TransformComponent>
        </TransformWrapper>
  
        {/* ピンの位置を表示 */}
        {pin && (
          <div
            className="pin"
            style={{
              position: "absolute",
              left: pin.x,
              top: pin.y,
              backgroundColor: "red",
              width: "10px",
              height: "10px",
              borderRadius: "50%",
              transform: "translate(-50%, -50%)",
              pointerEvents: "none",
            }}
          />
        )}
      </div>
  
      <button onClick={onBack} style={{ marginTop: "20px" }}>
        Back
      </button>
    </div>
  );
  export default ResultPage;
