import React, { useState, useRef, useEffect } from "react";
import { FixedSizeGrid as Grid } from "react-window";
import { TransformWrapper, TransformComponent } from "react-zoom-pan-pinch";

const App = () => {
  const [pin, setPin] = useState(null);
  const [scale, setScale] = useState(1);
  const [scrollOffset, setScrollOffset] = useState({ scrollLeft: 0, scrollTop: 0 });
  const [cursorPosition, setCursorPosition] = useState({ x: 0, y: 0 });
  const gridWrapperRef = useRef(null);

  const handleBack = () => {
    // "Back" ボタンが押されたときの処理
  };

  const handleShowResult = () => {
    // "結果" ボタンが押されたときの処理
  };

  const calculateDistance = (x1, y1, x2, y2) => {
    // 距離計算のロジック
    return Math.sqrt((x2 - x1) ** 2 + (y2 - y1) ** 2);
  };

  useEffect(() => {
    const handleMouseMove = (event) => {
      if (!gridWrapperRef.current) return;

      const { left, top } = gridWrapperRef.current.getBoundingClientRect();
      setCursorPosition({
        x: (event.clientX - left) / scale,
        y: (event.clientY - top) / scale,
      });
    };

    window.addEventListener("mousemove", handleMouseMove);

    return () => {
      window.removeEventListener("mousemove", handleMouseMove);
    };
  }, [scale]);

  return (
    <div className="container">
      {/* ボタンをグリッドの右外に配置 */}
      <button
        onClick={handleBack}
        style={{
          position: "absolute", // 絶対座標で配置
          top: 10, // 上から10ピクセルの位置
          right: 200, // 右から200ピクセルの位置（グリッドの右の外に配置）
          zIndex: 1000, // 他の要素の上に表示されるようにZインデックスを設定
          backgroundColor: "#007BFF",
          color: "#fff",
          border: "none",
          borderRadius: "5px",
          padding: "10px 20px",
          cursor: "pointer",
        }}
      >
        Back
      </button>
      <button
        onClick={handleShowResult}
        style={{
          position: "absolute", // 絶対座標で配置
          top: 10, // 上から10ピクセルの位置
          right: 10, // 右から10ピクセルの位置（グリッドの右の外に配置）
          zIndex: 1000, // 他の要素の上に表示されるようにZインデックスを設定
          backgroundColor: "#28A745",
          color: "#fff",
          border: "none",
          borderRadius: "5px",
          padding: "10px 20px",
          cursor: "pointer",
        }}
      >
        結果
      </button>
      <div ref={gridWrapperRef} className="grid-container" style={{ position: "relative" }}>
        <TransformWrapper
          options={{ limitToBounds: false }}
          onZoomStop={({ state }) => setScale(state.scale)}
          onPanningStop={({ state }) => {
            setScrollOffset({
              scrollLeft: -state.positionX,
              scrollTop: -state.positionY,
            });
          }}
        >
          <TransformComponent>
            <div
              style={{
                width: 50 * 500,
                height: 50 * 100,
                position: "relative",
                zIndex: 1,
              }}
            >
              <Grid
                className="Grid"
                columnCount={50}
                columnWidth={10}
                height={50 * 100}
                rowCount={50}
                rowHeight={10}
                width={50 * 100}
                style={{ overflow: "hidden", position: "absolute" }}
              >
                {({ columnIndex, rowIndex, style }) => (
                  <div
                    style={{
                      ...style,
                      display: "flex",
                      alignItems: "center",
                      justifyContent: "center",
                      backgroundColor:
                        (columnIndex + rowIndex) % 2 === 0
                          ? "#f8f8f0"
                          : "#ffffff",
                    }}
                  >
                    {/* ここにセルの内容を記述 */}
                  </div>
                )}
              </Grid>
            </div>
          </TransformComponent>
        </TransformWrapper>
      </div>
      {pin && (
        <div
          className="pin"
          style={{
            position: "absolute",
            left: pin.x * scale - scrollOffset.scrollLeft,
            top: pin.y * scale - scrollOffset.scrollTop,
            backgroundColor: "red",
            width: "10px",
            height: "10px",
            borderRadius: "50%",
            transform: "translate(-50%, -50%)",
            pointerEvents: "none",
          }}
        >
          <div
            style={{
              position: "absolute",
              bottom: "100%",
              left: "50%",
              transform: "translateX(-50%)",
              backgroundColor: "white",
              padding: "2px 5px",
              borderRadius: "3px",
              fontSize: "12px",
              boxShadow: "0 0 2px rgba(0,0,0,0.3)",
            }}
          >
            Distance:{" "}
            {pin
              ? calculateDistance(
                  pin.x,
                  pin.y,
                  referencePoint.x,
                  referencePoint.y
                )
              : "N/A"}{" "}
            px
          </div>
        </div>
      )}
      {/* カーソル位置の表示 */}
      <div
        className="cursor-position"
        style={{
          position: "absolute",
          left: cursorPosition.x * scale - scrollOffset.scrollLeft,
          top: cursorPosition.y * scale - scrollOffset.scrollTop,
          backgroundColor: "lightyellow",
          padding: "2px 5px",
          borderRadius: "3px",
          fontSize: "12px",
          boxShadow: "0 0 2px rgba(0,0,0,0.3)",
          pointerEvents: "none",
        }}
      >
        Cursor Position: X: {cursorPosition.x.toFixed(2)} px, Y:{" "}
        {cursorPosition.y.toFixed(2)} px
      </div>
    </div>
  );
};

export default App;
